mapper.select:
select

from tt_hotel
left join tt_hotel_supple  on tt_hotel.id=tt_hotel_supple.hotelId
where status != 3

  AND tt_hotel.id = #{id,jdbcType=BIGINT}


  AND hotelName  like concat('%',#{hotelname,jdbcType=VARCHAR},'%')


  AND hotelAddress like concat('%', #{hoteladdress,jdbcType=VARCHAR},'%')


  AND cityId = #{cityid,jdbcType=BIGINT}


  AND hotelPrice = #{hotelprice,jdbcType=BIGINT}


  AND hotelleve = #{hotelleve,jdbcType=INTEGER}


  AND tt_hotel.cid = #{cid,jdbcType=INTEGER}


AND status = #{status,jdbcType=INTEGER}

select
from tt_hotel left join tt_hotel_supple on tt_hotel.id=tt_hotel_supple.hotelId left join tt_hotel_des on tt_hotel_des.id=tt_hotel_supple.hotelId LEFT JOIN tt_city_area ON tt_hotel_supple.areaId=tt_city_area.id where status = 1
AND areaId = #{areaid,jdbcType=BIGINT}
AND cityId = #{cityid,jdbcType=BIGINT}
limit #{current},10
